nav.header__nav{
  display:flex !important;
  flex-direction:row;
  align-items:center}

div.d-inline-block a.header__category {
  display: flex !important;
  align-items: center;
  justify-content: center;
  height: 55px;}

/*removendo selo e-com
div.ecom-credits a{
  display:none;}*/

/*BANNERS BLOGS*/
div.blog-post p:has( > img[title="Banner Apresentação Blog"]){
  text-align:center;
}

/*VIDEOS BLOGS responsivos*/
.video-blog-responsive {
  position: relative;
  padding-bottom: 56.25%; /* Proporção do vídeo (16:9) */
  height: 0;
  overflow: hidden;
}
.video-blog-responsive iframe {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

/*TABELA BLOG | suporte prateleiras de vidro*/
div#container-tb-desktop{
  overflow-x:auto;
}

table.tb-desktop{
  margin:auto;
}
table.tb-desktop td, table.tb-desktop th{
  padding: 20px;
  text-align: center;
  border-bottom: solid 1px #ffedcc;
}
table.tb-desktop th{
  font-weight: bold;
  background-color: #f39422;
  color:#fff;
  border:solid 1px #fff;
  border-radius: 5px;
  padding: 12px;
}
table.tb-desktop td img{
  max-width: 100px;
}
table.tb-desktop td a{
  text-decoration: none;
  display: block;
}
table.tb-desktop td:last-child{
  border-right: solid 1px #ffedcc;
}
table.tb-desktop tr:last-child td:last-child{
  border-radius: 0 0 20px 0;
}
table.tb-desktop td:first-child{
  border-left: solid 1px #ffedcc;
}
table.tb-desktop tr:last-child td:first-child{
  border-radius: 0 0 0 20px;
}
.blog-post p.textoblog{font-size:17px; line-height:1.6;}  
.blog-post p.textoblog a{text-decoration:none;}
